import i18n from "i18next";
import { initReactI18next } from "react-i18next";
import LanguageDetector from "i18next-browser-languagedetector";
import enTranslations from "./locales/en.json" with { type: "json" };
import ruTranslations from "./locales/ru.json" with { type: "json" };

i18n
	.use(LanguageDetector)
	.use(initReactI18next)
	.init({
		resources: {
			en: {
				translation: enTranslations,
			},
			ru: {
				translation: ruTranslations,
			},
		},
		fallbackLng: "en",
		interpolation: {
			escapeValue: false,
		},
		detection: {
			order: ["localStorage", "navigator"],
			caches: ["localStorage"],
		},
	});

export default i18n;

/**
 * Comprehensive Tax Regulations Database for Multiple Countries
 * Covers major tax jurisdictions worldwide with accurate tax brackets and rules
 */

export interface TaxBracket {
	min: number;
	max: number | null; // null means infinity
	rate: number; // percentage
}

export interface TaxRegulation {
	country: string;
	countryCode: string;
	currency: string;
	currencySymbol: string;
	taxYear: number;
	incomeTax: {
		brackets: TaxBracket[];
