	corporateTax: {
		standardRate: number;
		smallBusinessRate?: number;
		threshold?: number;
	};
	vat: {
		standardRate: number;
		reducedRates: Array<{ rate: number; description: string }>;
	};
	socialSecurity: {
		employeeRate: number;
		employerRate: number;
		cap?: number;
	};
	capitalGainsTax: {
		shortTerm: number;
		longTerm: number;
	};
	dividendTax: {
		rate: number;
		allowance?: number;
	};
	propertyTax: {
		applicable: boolean;
		rateRange?: string;
	};
	inheritanceTax: {
		applicable: boolean;
		threshold?: number;
		rate?: number;
	};
	taxDeadlines: {
		individual: string;
		corporate: string;
		vat: string;
	};
	specialRules: string[];
}

