import { createFileRoute } from "@tanstack/react-router";
import { useState, useEffect } from "react";
import { useTranslation } from "react-i18next";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { DashboardView } from "@/components/DashboardView";
import { TransactionsView } from "@/components/TransactionsView";
import { AIAssistantView } from "@/components/AIAssistantView";
import { TaxCalculatorView } from "@/components/TaxCalculatorView";
import { TaxComplianceView } from "@/components/TaxComplianceView";
import { ReportsView } from "@/components/ReportsView";
import { RemindersView } from "@/components/RemindersView";
import { SettingsView } from "@/components/SettingsView";
import { InvoicesView } from "@/components/InvoicesView";
import { PricingPaywall } from "@/components/PricingPaywall";
import { LanguageSwitcher } from "@/components/LanguageSwitcher";
import { LayoutDashboard, Receipt, MessageSquare, Calculator, FileText, Bell, Settings, FileSpreadsheet, Globe } from "lucide-react";

export const Route = createFileRoute("/")({
	component: App,
});

function App() {
	const { t } = useTranslation();
	const [activeTab, setActiveTab] = useState("dashboard");
	const [isPurchased, setIsPurchased] = useState(false);
	const [isLoading, setIsLoading] = useState(true);

	// Check purchase status on mount
	useEffect(() => {
		const checkPurchaseStatus = () => {
			const purchased = localStorage.getItem('app_purchased') === 'true';
			setIsPurchased(purchased);
			setIsLoading(false);
		};

		checkPurchaseStatus();
	}, []);

	// Handle purchase completion
	const handlePurchaseComplete = () => {
		setIsPurchased(true);
	};

	// Show loading state briefly
	if (isLoading) {
		return (
			<div className="min-h-screen bg-gradient-to-br from-slate-900 to-slate-800 flex items-center justify-center">
				<div className="text-white text-xl">{t("app.loading")}</div>
			</div>
		);
	}

	// Show paywall if not purchased
	if (!isPurchased) {
		return <PricingPaywall onPurchaseComplete={handlePurchaseComplete} />;
	}

	return (
		<div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100">
			<header className="bg-white border-b border-slate-200 shadow-sm">
				<div className="container mx-auto px-4 py-4">
					<div className="flex items-center justify-between gap-3">
						<div className="flex items-center gap-3">
							<div className="bg-gradient-to-br from-emerald-500 to-teal-600 p-2 rounded-lg">
								<Calculator className="h-6 w-6 text-white" />
							</div>
							<div>
								<h1 className="text-2xl font-bold text-slate-900">{t("app.title")}</h1>
								<p className="text-sm text-slate-600">{t("app.subtitle")}</p>
							</div>
						</div>
						<LanguageSwitcher />
					</div>
				</div>
			</header>

			<main className="container mx-auto px-4 py-6">
				<Tabs value={activeTab} onValueChange={setActiveTab} className="space-y-6">
					<TabsList className="grid w-full grid-cols-9 lg:w-auto lg:inline-grid">
						<TabsTrigger value="dashboard" className="flex items-center gap-2">
							<LayoutDashboard className="h-4 w-4" />
							<span className="hidden sm:inline">{t("tabs.dashboard")}</span>
						</TabsTrigger>
						<TabsTrigger value="transactions" className="flex items-center gap-2">
							<Receipt className="h-4 w-4" />
							<span className="hidden sm:inline">{t("tabs.transactions")}</span>
						</TabsTrigger>
						<TabsTrigger value="invoices" className="flex items-center gap-2">
							<FileSpreadsheet className="h-4 w-4" />
							<span className="hidden sm:inline">{t("tabs.invoices")}</span>
						</TabsTrigger>
						<TabsTrigger value="assistant" className="flex items-center gap-2">
							<MessageSquare className="h-4 w-4" />
							<span className="hidden sm:inline">{t("tabs.assistant")}</span>
						</TabsTrigger>
						<TabsTrigger value="tax" className="flex items-center gap-2">
							<Calculator className="h-4 w-4" />
							<span className="hidden sm:inline">{t("tabs.tax")}</span>
						</TabsTrigger>
						<TabsTrigger value="compliance" className="flex items-center gap-2">
							<Globe className="h-4 w-4" />
							<span className="hidden sm:inline">{t("tabs.compliance")}</span>
						</TabsTrigger>
						<TabsTrigger value="reports" className="flex items-center gap-2">
							<FileText className="h-4 w-4" />
							<span className="hidden sm:inline">{t("tabs.reports")}</span>
						</TabsTrigger>
						<TabsTrigger value="reminders" className="flex items-center gap-2">
							<Bell className="h-4 w-4" />
							<span className="hidden sm:inline">{t("tabs.reminders")}</span>
						</TabsTrigger>
						<TabsTrigger value="settings" className="flex items-center gap-2">
							<Settings className="h-4 w-4" />
							<span className="hidden sm:inline">{t("tabs.settings")}</span>
						</TabsTrigger>
					</TabsList>

					<TabsContent value="dashboard" className="space-y-4">
						<DashboardView />
					</TabsContent>

					<TabsContent value="transactions">
						<TransactionsView />
					</TabsContent>

					<TabsContent value="invoices">
						<InvoicesView />
					</TabsContent>

					<TabsContent value="assistant">
						<AIAssistantView />
					</TabsContent>

					<TabsContent value="tax">
						<TaxCalculatorView />
					</TabsContent>

					<TabsContent value="compliance">
						<TaxComplianceView />
					</TabsContent>

					<TabsContent value="reports">
						<ReportsView />
					</TabsContent>

					<TabsContent value="reminders">
						<RemindersView />
					</TabsContent>

					<TabsContent value="settings">
						<SettingsView />
					</TabsContent>
				</Tabs>
			</main>
		</div>
	);
}

