				JPY: 149.50,
				CNY: 7.24,
				CHF: 0.88,
				CAD: 1.35,
				AUD: 1.52,
				INR: 83.20,
			};

			setExchangeRates(mockRates);
			setLastUpdated(new Date().toLocaleString());
		} catch (error) {
			console.error("Failed to fetch exchange rates:", error);
		} finally {
			setIsLoading(false);
		}
	};

	useEffect(() => {
		fetchExchangeRates();
	}, []);

	useEffect(() => {
		if (amount && exchangeRates[fromCurrency] && exchangeRates[toCurrency]) {
			const numAmount = parseFloat(amount);
			if (!isNaN(numAmount)) {
				// Convert from source currency to USD, then to target currency
				const usdAmount = numAmount / exchangeRates[fromCurrency];
				const result = usdAmount * exchangeRates[toCurrency];
				setConvertedAmount(result);
			}
		}
	}, [amount, fromCurrency, toCurrency, exchangeRates]);

	const handleSwapCurrencies = () => {
		setFromCurrency(toCurrency);
		setToCurrency(fromCurrency);
	};

	const getExchangeRate = () => {
		if (exchangeRates[fromCurrency] && exchangeRates[toCurrency]) {
			const rate = exchangeRates[toCurrency] / exchangeRates[fromCurrency];
			return rate.toFixed(4);
		}
		return "0";
	};

	return (
		<Card>
			<CardHeader>
				<CardTitle className="flex items-center gap-2">
					<TrendingUp className="h-5 w-5" />
					Currency Converter
				</CardTitle>
				<CardDescription>
					Convert between currencies with live exchange rates
					{lastUpdated && (
						<span className="block text-xs mt-1">Last updated: {lastUpdated}</span>
					)}
				</CardDescription>
			</CardHeader>
			<CardContent className="space-y-6">
				<div className="grid gap-4">
					{/* From Currency */}
					<div className="grid gap-2">
						<Label htmlFor="amount">Amount</Label>
						<div className="grid grid-cols-[2fr,1fr] gap-2">
							<Input
								id="amount"
								type="number"
								step="0.01"
								min="0"
								value={amount}
								onChange={(e) => setAmount(e.target.value)}
								placeholder="100.00"
								className="text-lg"
							/>
							<Select value={fromCurrency} onValueChange={setFromCurrency}>
								<SelectTrigger>
									<SelectValue />
								</SelectTrigger>
								<SelectContent>
									{POPULAR_CURRENCIES.map((currency) => (
										<SelectItem key={currency.code} value={currency.code}>
											{currency.code} {currency.symbol}
										</SelectItem>
									))}
								</SelectContent>
							</Select>
						</div>
					</div>

					{/* Swap Button */}
					<div className="flex justify-center">
						<Button
							variant="outline"
							size="icon"
							onClick={handleSwapCurrencies}
							className="rounded-full"
							disabled={isLoading}
						>
							<ArrowRightLeft className="h-4 w-4" />
						</Button>
					</div>

					{/* To Currency */}
					<div className="grid gap-2">
						<Label htmlFor="converted">Converted Amount</Label>
						<div className="grid grid-cols-[2fr,1fr] gap-2">
							<div className="relative">
								<Input
									id="converted"
									type="text"
									value={isLoading ? "Converting..." : convertedAmount.toFixed(2)}
									disabled
									className="text-lg font-bold bg-slate-50"
								/>
								{isLoading && (
									<Loader2 className="absolute right-3 top-3 h-5 w-5 animate-spin text-slate-400" />
								)}
							</div>
							<Select value={toCurrency} onValueChange={setToCurrency}>
								<SelectTrigger>
									<SelectValue />
								</SelectTrigger>
								<SelectContent>
									{POPULAR_CURRENCIES.map((currency) => (
										<SelectItem key={currency.code} value={currency.code}>
											{currency.code} {currency.symbol}
										</SelectItem>
									))}
								</SelectContent>
							</Select>
						</div>
					</div>
				</div>

				{/* Exchange Rate Info */}
				<div className="bg-blue-50 p-4 rounded-lg">
					<p className="text-sm text-blue-900">
						<span className="font-medium">Exchange Rate:</span> 1 {fromCurrency} = {getExchangeRate()} {toCurrency}
					</p>
				</div>

				{/* Quick Convert Buttons */}
				<div className="space-y-2">
					<Label className="text-xs text-slate-600">Quick amounts:</Label>
					<div className="flex gap-2 flex-wrap">
						{[100, 500, 1000, 5000, 10000].map((quickAmount) => (
							<Button
								key={quickAmount}
								variant="outline"
								size="sm"
								onClick={() => setAmount(quickAmount.toString())}
							>
								{quickAmount.toLocaleString()}
							</Button>
						))}
					</div>
				</div>

				{/* Refresh Button */}
				<Button
					variant="outline"
					onClick={fetchExchangeRates}
					disabled={isLoading}
					className="w-full"
				>
					{isLoading ? (
						<>
							<Loader2 className="h-4 w-4 mr-2 animate-spin" />
							Updating...
						</>
					) : (
						"Refresh Rates"
					)}
				</Button>
			</CardContent>
		</Card>
	);
}

