import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Wallet, CheckCircle, AlertCircle, Loader2 } from "lucide-react";
import { toast } from "sonner";

declare global {
	interface Window {
		ethereum?: {
			isMetaMask?: boolean;
			request: (args: { method: string; params?: unknown[] }) => Promise<unknown>;
			on?: (event: string, callback: (...args: unknown[]) => void) => void;
			removeListener?: (event: string, callback: (...args: unknown[]) => void) => void;
		};
	}
}

interface MetamaskButtonProps {
	amount: number;
	onSuccess?: () => void;
	onError?: (error: Error) => void;
}

export function MetamaskButton({ amount, onSuccess, onError }: MetamaskButtonProps) {
	const [isMetaMaskInstalled, setIsMetaMaskInstalled] = useState(false);
	const [isConnected, setIsConnected] = useState(false);
	const [account, setAccount] = useState<string>("");
	const [isProcessing, setIsProcessing] = useState(false);

	useEffect(() => {
		// Check if MetaMask is installed
		if (typeof window.ethereum !== "undefined" && window.ethereum.isMetaMask) {
			setIsMetaMaskInstalled(true);
			checkConnection();
		}
	}, []);

	const checkConnection = async () => {
		if (!window.ethereum) return;

		try {
			const accounts = await window.ethereum.request({ method: "eth_accounts" }) as string[];
			if (accounts.length > 0) {
				setAccount(accounts[0]);
				setIsConnected(true);
			}
		} catch (error) {
			console.error("Error checking MetaMask connection:", error);
		}
	};

	const connectWallet = async () => {
		if (!window.ethereum) {
			toast.error("MetaMask is not installed");
			return;
		}

		try {
			const accounts = await window.ethereum.request({
				method: "eth_requestAccounts",
			}) as string[];

			if (accounts.length > 0) {
				setAccount(accounts[0]);
				setIsConnected(true);
				toast.success("Wallet connected successfully!");
			}
		} catch (error) {
			console.error("Error connecting to MetaMask:", error);
			toast.error("Failed to connect wallet");
		}
	};

	const handlePayment = async () => {
		if (!isConnected || !window.ethereum) {
			toast.error("Please connect your wallet first");
			return;
		}

		setIsProcessing(true);

		try {
			// Convert USD to ETH (mock conversion rate: 1 ETH = $2000)
			const ethAmount = (amount / 2000).toFixed(6);
			const weiAmount = BigInt(Math.floor(parseFloat(ethAmount) * 1e18)).toString(16);

			// Recipient address (replace with your actual address)
			const recipientAddress = "0x742d35Cc6634C0532925a3b844Bc9e7595f0bEb7";

			const transactionParameters = {
				to: recipientAddress,
				from: account,
				value: `0x${weiAmount}`,
			};

			const txHash = await window.ethereum.request({
				method: "eth_sendTransaction",
				params: [transactionParameters],
			}) as string;

			toast.success(`Transaction sent! Hash: ${txHash.slice(0, 10)}...`);

			// Save purchase info
			localStorage.setItem("app_purchased", "true");
			localStorage.setItem("purchase_platform", "metamask");
			localStorage.setItem("purchase_date", new Date().toLocaleDateString());

			if (onSuccess) {
				onSuccess();
			}
		} catch (error) {
			console.error("Payment error:", error);
			const err = error as Error;
			toast.error(`Payment failed: ${err.message || "Unknown error"}`);
			if (onError) {
				onError(err);
			}
		} finally {
			setIsProcessing(false);
		}
	};

	if (!isMetaMaskInstalled) {
		return (
			<Button
				variant="outline"
				className="w-full"
				onClick={() => {
					window.open("https://metamask.io/download/", "_blank");
				}}
			>
				<Wallet className="h-4 w-4 mr-2" />
				Install MetaMask
			</Button>
		);
	}

	if (!isConnected) {
		return (
			<Button
				variant="outline"
				className="w-full border-orange-300 hover:bg-orange-50"
				onClick={connectWallet}
			>
				<Wallet className="h-4 w-4 mr-2 text-orange-600" />
				Connect MetaMask
			</Button>
		);
	}

	return (
		<div className="space-y-2">
			<div className="flex items-center gap-2 text-sm text-emerald-600 bg-emerald-50 p-2 rounded">
				<CheckCircle className="h-4 w-4" />
				<span className="flex-1 truncate">Connected: {account}</span>
			</div>
			<Button
				className="w-full bg-orange-500 hover:bg-orange-600"
				onClick={handlePayment}
				disabled={isProcessing}
			>
				{isProcessing ? (
					<>
						<Loader2 className="h-4 w-4 mr-2 animate-spin" />
						Processing...
					</>
				) : (
					<>
						<Wallet className="h-4 w-4 mr-2" />
						Pay ${amount} with MetaMask
					</>
				)}
			</Button>
		</div>
	);
}

