import { createFileRoute } from "@tanstack/react-router";
import { useState, useEffect } from "react";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { DashboardView } from "@/components/DashboardView";
import { TransactionsView } from "@/components/TransactionsView";
import { AIAssistantView } from "@/components/AIAssistantView";
import { TaxCalculatorView } from "@/components/TaxCalculatorView";
import { ReportsView } from "@/components/ReportsView";
import { RemindersView } from "@/components/RemindersView";
import { SettingsView } from "@/components/SettingsView";
import { PricingPaywall } from "@/components/PricingPaywall";
import { LayoutDashboard, Receipt, MessageSquare, Calculator, FileText, Bell, Settings } from "lucide-react";

export const Route = createFileRoute("/")({
	component: App,
});

function App() {
	const [activeTab, setActiveTab] = useState("dashboard");
	const [isPurchased, setIsPurchased] = useState(false);
	const [isLoading, setIsLoading] = useState(true);

	// Check purchase status on mount
	useEffect(() => {
		const checkPurchaseStatus = () => {
			const purchased = localStorage.getItem('app_purchased') === 'true';
			setIsPurchased(purchased);
			setIsLoading(false);
		};

		checkPurchaseStatus();
	}, []);

	// Handle purchase completion
	const handlePurchaseComplete = () => {
		setIsPurchased(true);
	};

	// Show loading state briefly
	if (isLoading) {
		return (
			<div className="min-h-screen bg-gradient-to-br from-slate-900 to-slate-800 flex items-center justify-center">
				<div className="text-white text-xl">Loading...</div>
			</div>
		);
	}

	// Show paywall if not purchased
	if (!isPurchased) {
		return <PricingPaywall onPurchaseComplete={handlePurchaseComplete} />;
	}

	return (
		<div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100">
			<header className="bg-white border-b border-slate-200 shadow-sm">
				<div className="container mx-auto px-4 py-4">
					<div className="flex items-center gap-3">
						<div className="bg-gradient-to-br from-emerald-500 to-teal-600 p-2 rounded-lg">
							<Calculator className="h-6 w-6 text-white" />
						</div>
						<div>
							<h1 className="text-2xl font-bold text-slate-900">AI Accountant</h1>
							<p className="text-sm text-slate-600">Your Personal Financial Assistant</p>
						</div>
					</div>
				</div>
			</header>

			<main className="container mx-auto px-4 py-6">
				<Tabs value={activeTab} onValueChange={setActiveTab} className="space-y-6">
					<TabsList className="grid w-full grid-cols-7 lg:w-auto lg:inline-grid">
						<TabsTrigger value="dashboard" className="flex items-center gap-2">
							<LayoutDashboard className="h-4 w-4" />
							<span className="hidden sm:inline">Dashboard</span>
						</TabsTrigger>
						<TabsTrigger value="transactions" className="flex items-center gap-2">
							<Receipt className="h-4 w-4" />
							<span className="hidden sm:inline">Transactions</span>
						</TabsTrigger>
						<TabsTrigger value="assistant" className="flex items-center gap-2">
							<MessageSquare className="h-4 w-4" />
							<span className="hidden sm:inline">AI Assistant</span>
						</TabsTrigger>
						<TabsTrigger value="tax" className="flex items-center gap-2">
							<Calculator className="h-4 w-4" />
							<span className="hidden sm:inline">Tax Calculator</span>
						</TabsTrigger>
						<TabsTrigger value="reports" className="flex items-center gap-2">
							<FileText className="h-4 w-4" />
							<span className="hidden sm:inline">Reports</span>
						</TabsTrigger>
						<TabsTrigger value="reminders" className="flex items-center gap-2">
							<Bell className="h-4 w-4" />
							<span className="hidden sm:inline">Reminders</span>
						</TabsTrigger>
						<TabsTrigger value="settings" className="flex items-center gap-2">
							<Settings className="h-4 w-4" />
							<span className="hidden sm:inline">Settings</span>
						</TabsTrigger>
					</TabsList>

					<TabsContent value="dashboard" className="space-y-4">
						<DashboardView />
					</TabsContent>

					<TabsContent value="transactions">
						<TransactionsView />
					</TabsContent>

					<TabsContent value="assistant">
						<AIAssistantView />
					</TabsContent>

					<TabsContent value="tax">
						<TaxCalculatorView />
					</TabsContent>

					<TabsContent value="reports">
						<ReportsView />
					</TabsContent>

					<TabsContent value="reminders">
						<RemindersView />
					</TabsContent>

					<TabsContent value="settings">
						<SettingsView />
					</TabsContent>
				</Tabs>
			</main>
		</div>
	);
}

import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Check, Smartphone, Apple, CreditCard, Wallet } from "lucide-react";
import { PayPalButton } from "@/components/PayPalButton";
import { StripeCardButton } from "@/components/StripeCardButton";
import { ApplePayButton } from "@/components/ApplePayButton";
import { GooglePayButton } from "@/components/GooglePayButton";
import { useState } from "react";
import { type PayPalCaptureResult } from "@/components/PayPalButton";

interface PricingPaywallProps {
	onPurchaseComplete?: () => void;
}

export function PricingPaywall({ onPurchaseComplete }: PricingPaywallProps) {
	const [showSuccessMessage, setShowSuccessMessage] = useState(false);
	const [paymentMethod, setPaymentMethod] = useState<'card' | 'wallet' | 'appstore'>('card');

	const handlePaymentSuccess = (details: PayPalCaptureResult | unknown) => {
		console.log("Payment successful:", details);
		setShowSuccessMessage(true);
		// Auto-unlock after showing success message
		setTimeout(() => {
			onPurchaseComplete?.();
		}, 2000);
	};

	const handlePaymentError = (error: Error) => {
		console.error("Payment error:", error);
		alert("Payment failed. Please try again or contact support.");
	};

	const handlePaymentCancel = () => {
		console.log("Payment cancelled by user");
	};

	const handleAppStorePurchase = () => {
		// In production, this would redirect to actual App Store listing
		// For now, we'll simulate a purchase and unlock
		const appStoreUrl = "https://apps.apple.com/app/ai-accountant/id123456789";

		// Open App Store in new tab
		window.open(appStoreUrl, '_blank');

		// For demo purposes, simulate purchase completion after a delay
		// In production, this would be verified via App Store receipt validation
		setTimeout(() => {
			if (confirm("Did you complete the purchase on App Store?")) {
				localStorage.setItem('app_purchased', 'true');
				localStorage.setItem('purchase_platform', 'appstore');
				localStorage.setItem('purchase_date', new Date().toLocaleDateString());
				onPurchaseComplete?.();
			}
		}, 2000);
	};

	const handleGooglePlayPurchase = () => {
		// In production, this would redirect to actual Google Play listing
		// For now, we'll simulate a purchase and unlock
		const googlePlayUrl = "https://play.google.com/store/apps/details?id=com.aiaccountant.app";

		// Open Google Play in new tab
		window.open(googlePlayUrl, '_blank');

		// For demo purposes, simulate purchase completion after a delay
		// In production, this would be verified via Google Play receipt validation
		setTimeout(() => {
			if (confirm("Did you complete the purchase on Google Play?")) {
				localStorage.setItem('app_purchased', 'true');
				localStorage.setItem('purchase_platform', 'googleplay');
				localStorage.setItem('purchase_date', new Date().toLocaleDateString());
				onPurchaseComplete?.();
			}
		}, 2000);
	};

	const features = [
		"AI-powered financial insights and advice",
		"Unlimited transaction tracking",
		"Smart tax calculation and optimization",
		"Automated financial reports",
		"Bill reminders and notifications",
		"Multi-currency support",
		"Bank-level encryption and security",
		"Priority customer support",
		"Lifetime updates and improvements",
	];

	return (
		<div className="min-h-screen bg-gradient-to-br from-slate-900 via-slate-800 to-slate-900 flex items-center justify-center p-4">
			<div className="max-w-4xl w-full">
				<div className="text-center mb-8">
					<div className="inline-flex items-center gap-3 mb-4">
						<div className="bg-gradient-to-br from-emerald-500 to-teal-600 p-3 rounded-xl">
							<Smartphone className="h-8 w-8 text-white" />
						</div>
						<h1 className="text-4xl font-bold text-white">AI Accountant</h1>
					</div>
					<p className="text-xl text-slate-300">Your Personal Financial Assistant</p>
				</div>

				<Card className="border-slate-700 bg-slate-800/50 backdrop-blur">
					<CardHeader className="text-center pb-8">
						<div className="mb-4">
							<Badge variant="secondary" className="mb-2 bg-emerald-500/20 text-emerald-400 border-emerald-500/50">
								One-Time Purchase
							</Badge>
						</div>
						<CardTitle className="text-5xl font-bold text-white mb-2">
							$50
						</CardTitle>
						<CardDescription className="text-lg text-slate-300">
							Lifetime access to all features
						</CardDescription>
					</CardHeader>

					<CardContent className="space-y-8">
						{/* Features List */}
						<div className="grid gap-3">
							{features.map((feature, index) => (
								<div key={index} className="flex items-start gap-3">
									<div className="bg-emerald-500/20 rounded-full p-1 mt-0.5">
										<Check className="h-4 w-4 text-emerald-400" />
									</div>
									<span className="text-slate-200">{feature}</span>
								</div>
							))}
						</div>

						{/* Purchase Buttons */}
						<div className="space-y-4 pt-4">
							{showSuccessMessage && (
								<div className="p-4 bg-emerald-500/20 border border-emerald-500/50 rounded-lg mb-4">
									<p className="text-emerald-400 text-center font-semibold">
										âœ“ Payment successful! Unlocking your account...
									</p>
								</div>
							)}

							<p className="text-center text-sm text-slate-400 mb-2">
								Choose your payment method:
							</p>
							<p className="text-center text-xs text-emerald-400 mb-4">
								âœ“ Secure checkout â€¢ âœ“ Instant access â€¢ âœ“ All methods accepted
							</p>

							{/* Payment Method Selector */}
							<Tabs value={paymentMethod} onValueChange={(v) => setPaymentMethod(v as typeof paymentMethod)} className="w-full">
								<TabsList className="grid w-full grid-cols-3 mb-4">
									<TabsTrigger value="card" className="flex items-center gap-2">
										<CreditCard className="h-4 w-4" />
										<span className="hidden sm:inline">Cards</span>
									</TabsTrigger>
									<TabsTrigger value="wallet" className="flex items-center gap-2">
										<Wallet className="h-4 w-4" />
										<span className="hidden sm:inline">Wallets</span>
									</TabsTrigger>
									<TabsTrigger value="appstore" className="flex items-center gap-2">
										<Smartphone className="h-4 w-4" />
										<span className="hidden sm:inline">App Store</span>
									</TabsTrigger>
								</TabsList>

								<TabsContent value="card" className="space-y-3">
									<StripeCardButton
										amount="50.00"
										description="AI Accountant - Lifetime Access"
										onSuccess={handlePaymentSuccess}
										onError={handlePaymentError}
										onCancel={handlePaymentCancel}
									/>
								</TabsContent>

								<TabsContent value="wallet" className="space-y-3">
									<PayPalButton
										amount="50.00"
										description="AI Accountant - Lifetime Access"
										onSuccess={handlePaymentSuccess}
										onError={handlePaymentError}
										onCancel={handlePaymentCancel}
									/>

									<div className="relative py-2">
										<div className="absolute inset-0 flex items-center">
											<div className="w-full border-t border-slate-700"></div>
										</div>
										<div className="relative flex justify-center text-xs">
											<span className="bg-slate-800 px-2 text-slate-500">or</span>
										</div>
									</div>

									<ApplePayButton
										amount="50.00"
										description="AI Accountant - Lifetime Access"
										onSuccess={handlePaymentSuccess}
										onError={handlePaymentError}
										onCancel={handlePaymentCancel}
									/>

									<GooglePayButton
										amount="50.00"
										description="AI Accountant - Lifetime Access"
										onSuccess={handlePaymentSuccess}
										onError={handlePaymentError}
										onCancel={handlePaymentCancel}
									/>
								</TabsContent>

								<TabsContent value="appstore" className="space-y-3">
									<Button
										onClick={handleAppStorePurchase}
										size="lg"
										variant="outline"
										className="w-full border-slate-600 bg-slate-700/50 hover:bg-slate-700 text-white text-lg py-6"
									>
										<Apple className="h-6 w-6 mr-3" />
										Purchase on App Store
									</Button>

									<Button
										onClick={handleGooglePlayPurchase}
										size="lg"
										variant="outline"
										className="w-full border-slate-600 bg-slate-700/50 hover:bg-slate-700 text-white text-lg py-6"
									>
										<Smartphone className="h-6 w-6 mr-3" />
										Purchase on Google Play
									</Button>
								</TabsContent>
							</Tabs>
						</div>

						{/* Trust Badges */}
						<div className="pt-6 border-t border-slate-700">
							<div className="grid grid-cols-3 gap-4 text-center">
								<div>
									<p className="text-2xl font-bold text-emerald-400">ðŸ”’</p>
									<p className="text-xs text-slate-400 mt-1">Secure Payment</p>
								</div>
								<div>
									<p className="text-2xl font-bold text-emerald-400">âœ“</p>
									<p className="text-xs text-slate-400 mt-1">Instant Access</p>
								</div>
								<div>
									<p className="text-2xl font-bold text-emerald-400">âˆž</p>
									<p className="text-xs text-slate-400 mt-1">Lifetime Updates</p>
								</div>
							</div>
						</div>

						<p className="text-center text-xs text-slate-500">
							By purchasing, you agree to our Terms of Service and Privacy Policy.
							<br />
							All payments are processed securely. We accept credit cards, PayPal, Apple Pay, Google Pay, and app store purchases.
						</p>
					</CardContent>
				</Card>

				<p className="text-center text-sm text-slate-400 mt-6">
					Need help? Contact support@aiaccountant.app
				</p>
			</div>
		</div>
	);
}

import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { TransactionORM, TransactionType } from "@/components/data/orm/orm_transaction";
import { FinancialReminderORM } from "@/components/data/orm/orm_financial_reminder";
import { TaxProfileORM } from "@/components/data/orm/orm_tax_profile";
import { APP_CONFIG } from "@/main";
import { ArrowUpCircle, ArrowDownCircle, TrendingUp, AlertCircle } from "lucide-react";
import { Alert, AlertDescription, AlertTitle } from "@/components/ui/alert";
import { Skeleton } from "@/components/ui/skeleton";

export function DashboardView() {
	const userId = APP_CONFIG.userId;

	const { data: transactions, isLoading: transactionsLoading } = useQuery({
		queryKey: ["transactions", userId],
		queryFn: async () => {
			const orm = TransactionORM.getInstance();
			const [results] = await orm.listTransaction();
			return results.filter((t) => t.user_id === userId);
		},
	});

	const { data: reminders, isLoading: remindersLoading } = useQuery({
		queryKey: ["reminders", userId],
		queryFn: async () => {
			const orm = FinancialReminderORM.getInstance();
			const [results] = await orm.listFinancialReminder();
			const userReminders = results.filter((r) => r.user_id === userId);
			return userReminders.filter((r) => !r.is_completed);
		},
	});

	const { data: taxProfile } = useQuery({
		queryKey: ["taxProfile", userId],
		queryFn: async () => {
			if (!userId) return null;
			const orm = TaxProfileORM.getInstance();
			const results = await orm.getTaxProfileByUserId(userId);
			return results[0] || null;
		},
		enabled: !!userId,
	});

	const totalIncome = transactions
		?.filter((t) => t.type === TransactionType.Income)
		.reduce((sum, t) => sum + t.amount, 0) || 0;

	const totalExpenses = transactions
		?.filter((t) => t.type === TransactionType.Expense)
		.reduce((sum, t) => sum + t.amount, 0) || 0;

	const netAmount = totalIncome - totalExpenses;

	const upcomingReminders = reminders?.slice(0, 5) || [];

	if (transactionsLoading || remindersLoading) {
		return (
			<div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
				{[...Array(4)].map((_, i) => (
					<Card key={i}>
						<CardHeader>
							<Skeleton className="h-4 w-24" />
						</CardHeader>
						<CardContent>
							<Skeleton className="h-8 w-32" />
						</CardContent>
					</Card>
				))}
			</div>
		);
	}

	return (
		<div className="space-y-6">
			<div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
				<Card>
					<CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
						<CardTitle className="text-sm font-medium">Total Income</CardTitle>
						<ArrowUpCircle className="h-4 w-4 text-emerald-600" />
					</CardHeader>
					<CardContent>
						<div className="text-2xl font-bold text-emerald-600">
							${totalIncome.toLocaleString('en-US', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}
						</div>
						<p className="text-xs text-slate-600 mt-1">All time earnings</p>
					</CardContent>
				</Card>

				<Card>
					<CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
						<CardTitle className="text-sm font-medium">Total Expenses</CardTitle>
						<ArrowDownCircle className="h-4 w-4 text-rose-600" />
					</CardHeader>
					<CardContent>
						<div className="text-2xl font-bold text-rose-600">
							${totalExpenses.toLocaleString('en-US', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}
						</div>
						<p className="text-xs text-slate-600 mt-1">All time spending</p>
					</CardContent>
				</Card>

				<Card>
					<CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
						<CardTitle className="text-sm font-medium">Net Amount</CardTitle>
						<TrendingUp className={`h-4 w-4 ${netAmount >= 0 ? 'text-emerald-600' : 'text-rose-600'}`} />
					</CardHeader>
					<CardContent>
						<div className={`text-2xl font-bold ${netAmount >= 0 ? 'text-emerald-600' : 'text-rose-600'}`}>
							${netAmount.toLocaleString('en-US', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}
						</div>
						<p className="text-xs text-slate-600 mt-1">Income - Expenses</p>
					</CardContent>
				</Card>

				<Card>
					<CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
						<CardTitle className="text-sm font-medium">Transactions</CardTitle>
						<AlertCircle className="h-4 w-4 text-blue-600" />
					</CardHeader>
					<CardContent>
						<div className="text-2xl font-bold text-blue-600">{transactions?.length || 0}</div>
						<p className="text-xs text-slate-600 mt-1">Total records</p>
					</CardContent>
				</Card>
			</div>

			{upcomingReminders.length > 0 && (
				<Card>
					<CardHeader>
						<CardTitle>Upcoming Reminders</CardTitle>
						<CardDescription>Tasks that need your attention</CardDescription>
					</CardHeader>
					<CardContent>
						<div className="space-y-3">
							{upcomingReminders.map((reminder) => (
								<Alert key={reminder.id}>
									<AlertCircle className="h-4 w-4" />
									<AlertTitle>{reminder.title}</AlertTitle>
									<AlertDescription>
										{reminder.description && <span>{reminder.description} â€¢ </span>}
										Due: {new Date(reminder.due_date).toLocaleDateString()}
									</AlertDescription>
								</Alert>
							))}
						</div>
					</CardContent>
				</Card>
			)}

			{!taxProfile && (
				<Alert>
					<AlertCircle className="h-4 w-4" />
					<AlertTitle>Tax Profile Not Set</AlertTitle>
					<AlertDescription>
						Set up your tax profile in the Tax Calculator tab to get personalized tax estimates and recommendations.
					</AlertDescription>
				</Alert>
			)}
		</div>
	);
}

